import React, { useState, useEffect } from 'react'
import { View, Text, StyleSheet, Image, ImageBackground } from 'react-native';
import { ListItem, Icon, Card, Button } from 'react-native-elements';
import { InteractionManager } from 'react-native';



export default function Category_list({ navigation }) {


    const [categories, setcategories] = useState([]);


    useEffect(() => {
        fillData();

    }, []);

    const fillData = () => {
        fetch('https://northwind.vercel.app/api/categories')
            .then((res) => res.json())
            .then((data) => {

                setcategories(data);

            })
    }

    const deleteCategory = (id) => {

        let requestoptions = {
            method: 'DELETE',
            body: JSON.stringify({ id: id })
        }

        fetch('https://northwind.vercel.app/api/categories/' + id, requestoptions)
            .then((res) => res.json())
            .then((data) => {
                fillData(data);
            })

    }

    const goaddCat = () => {
        navigation.navigate('addCat')
    }


    return (
        <View style={styles.container} >
            <ImageBackground source={require('../Images/background.jpg')} style={styles.image}></ImageBackground>
            {

                categories.slice(0, 7).map((item) => (
                    <Card>


                        <View >

                                <ListItem.Content  >
                                    <View>
                                        <ListItem.Title>Name: {item.name}</ListItem.Title>
                                        <ListItem.Subtitle>Description: {item.description}</ListItem.Subtitle>

                                    </View>

                                    <View >
                                        <Icon name='delete' onPress={() => deleteCategory(item.id)} />
                                    </View>
                                </ListItem.Content>


                        </View>

                    </Card>
                ))
            }


            <Button onPress={() => goaddCat()} title='Go to add Category !' ></Button>

        </View>

    )

}

