import React, { useState, useEffect } from 'react'
import { View, Text, StyleSheet, Image, ImageBackground,TouchableOpacity } from 'react-native';
import { ListItem, Icon, Card, Button } from 'react-native-elements';
import { InteractionManager } from 'react-native';



export default function Category_list({ navigation }) {


    const [categories, setcategories] = useState([]);


    useEffect(() => {
        fillData();

    }, []);

    const fillData = () => {
        fetch('https://northwind.vercel.app/api/categories')
            .then((res) => res.json())
            .then((data) => {

                setcategories(data);

            })
    }

    const deleteCategory = (id) => {

        let requestoptions = {
            method: 'DELETE',
            body: JSON.stringify({ id: id })
        }

        fetch('https://northwind.vercel.app/api/categories/' + id, requestoptions)
            .then((res) => res.json())
            .then((data) => {
                fillData(data);
            })

    }

    const goaddCat = () => {
        navigation.navigate('addCat')
    }


    return (
        <View  >

            {

                categories.slice(0, 7).map((item) => (
                    <Card >

                        <View style={styles.card} >

                            <ListItem >
                                <ListItem.Content  >
                                    <View>
                                        <ListItem.Title style={styles.textStyle}>Name: {item.name}</ListItem.Title>
                                        <Card.Image style={styles.image} source={require('../Images/background.jpg')} />

                                        <Card.Divider/>
                                        <ListItem.Subtitle >Description: {item.description}</ListItem.Subtitle>


                                    </View>




                                    <TouchableHighlight onPress={()=>{deleteCategory(item.id)}}>
                                    <View >

                                        <Icon name='delete'/>
                                       <text>Delete </text>

                                    </View>
                                    </TouchableHighlight>
                                </ListItem.Content>

                            </ListItem>

                        </View>

                    </Card>
                ))

            }



            <Button onPress={() => goaddCat()} title='Go to add Category !' ></Button>

        </View>

    )

}


