import React, { useState, useEffect } from 'react'
import { InteractionManager, View, Text, StyleSheet, Button, Alert, TouchableHighlight, TouchableOpacity, Image, ImageBackground, StatusBar } from 'react-native';
import { ListItem, Icon, Card } from 'react-native-elements';
import { TextInput } from 'react-native-gesture-handler';
import TopBar from '../components/TopBar';
import { BackgroundImage } from 'react-native-elements/dist/config';

function Product_detail({ route, navigation }) {

  const { id } = route.params;

  const [products, setProducts] = useState([]);


  useEffect(() => {
    fillData(id);

  }, []);

  const fillData = (id) => {
    fetch('https://northwind.vercel.app/api/products/' + id)
      .then((res) => res.json())
      .then((products) => {

        setProducts(products);
      })
  }


  return (
    <View style={styles.container}>
      <TopBar />
      <ImageBackground source={require('../Images/background.jpg')} style={styles.image1} >


        <Card containerStyle={styles.card__today} backgroundColor={'black'}>
         
            <ListItem>
              <ListItem.Content>
                <View>

                  <ListItem.Title style={styles.textStyle}>{products.name}</ListItem.Title>
                  <Card.Divider />

                  <View >
                    <Image style={styles.image2} source={require('../Images/food5.jpg')} />
                  </View>
                  <ListItem.Subtitle >Price: {products.unitPrice}$</ListItem.Subtitle>
                  <ListItem.Subtitle>Quantity Per Unit: {products.quantityPerUnit}</ListItem.Subtitle>
                  <ListItem.Subtitle>Stock in: {products.unitsInStock}</ListItem.Subtitle>


                </View>
              </ListItem.Content>
            </ListItem>


        </Card>
      </ImageBackground>
    </View>
  );
}
export default Product_detail

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'flex-end',

  },
  card: {
    backgroundColor: '#fff',
    width: "%100",
    height: 280,
    /* shadowColor: "#000",
     shadowOffset: {
         width: 5,
         height: 5,
     },
     shadowOpacity: 4.32,
     shadowRadius: 5.46,
     elevation: 9,*/

  },

  image1: {
    flex: 1,
    resizeMode: "cover",
    justifyContent: "center",
    width: '100%',
    height: '100%',
  },
  textStyle: {
    color: "Black",
    fontSize: 16,
    fontWeight: 'bold',
    fontFamily: 'Tahoma'

  },
  image2: {
    // flex: 1,
    //  resizeMode: "",
    justifyContent: "center",

    width: 100,
    height: 100,
    marginVertical: 10,
    marginHorizontal: 100,

  },
  imageView: {
    justifyContent: "center",
    width: 100,
    height: 100,
    borderRadius: 10,
    marginBottom: 10


  },
  buttonText: {
    color: "#EEEDE7",
    fontSize: 16,
    fontWeight: '500',
    fontFamily: 'Roboto'


  },

});
