import React, { useState, useEffect } from 'react'
import { View, Text, StyleSheet } from 'react-native';
import {ListItem, Icon} from 'react-native-elements';
import {InteractionManager} from 'react-native';
import {Button} from 'react-native';
import color from 'color';

export default function Category_list({ navigation }) {


    const [categories, setcategories] = useState([]);


    useEffect(() => {
        fillData();

    }, []);

    const fillData = () => {
        fetch('https://northwind.vercel.app/api/categories')
        .then((res) => res.json())
        .then((data) => {

           setcategories(data);
        })
    }

    const deleteCategory = (id) => {

        let requestoptions = {
            method : 'DELETE',
            body: JSON.stringify({id:id})
        }

        fetch('https://northwind.vercel.app/api/categories/' + id, requestoptions)
            .then((res) => res.json ())
            .then((data) => {
                 fillData(data);
        })

    }

    const goaddCat = () =>{
        navigation.navigate('addCat')
    }

    return (
        <View  style = {styles.MainContainer} >
                {

                    categories.slice(0,7).map((item)  => (
                        <View style={{padding: 2, flex:3,backgroundColor: '#FFEB3B'}} >
                        <ListItem >
                        <ListItem.Content >
                         <View>
                          <ListItem.Title style={{padding: 2, }}>Name:     {item.name}</ListItem.Title>
                          <ListItem.Title style={{padding: 2, }}>Description:   {item.description}</ListItem.Title>
                        </View>
                          <View style={{flex:3, alignSelf:'center'}}>
                          <Icon name='delete' onPress={() => deleteCategory(item.id)} />
                          </View>
                        </ListItem.Content>
                      </ListItem>
                      </View>
                    ))
                    }
                <Button onPress={() => goaddCat()} title='Go to add Category !'></Button>

        </View>

    )

}

const styles = StyleSheet.create({

    MainContainer: 
    {

    flex: 1,

    // Set content's vertical alignment.
    justifyContent: 'center',

    // Set content's horizontal alignment.
    alignItems: 'center',

    // Set hex color code here.
    backgroundColor: '#FFEB3B',

    },


});
