import React, { useState, useEffect } from 'react'
import { View, Text, StyleSheet, Image } from 'react-native';
import {ListItem, Icon, Card , Button} from 'react-native-elements';
import {InteractionManager} from 'react-native';


const users = [
    {
       name: 'brynn',
       avatar: 'https://s3.amazonaws.com/uifaces/faces/twitter/brynn/128.jpg'
    }]

export default function Category_list({ navigation }) {


    const [categories, setcategories] = useState([]);


    useEffect(() => {
        fillData();

    }, []);

    const fillData = () => {
        fetch('https://northwind.vercel.app/api/categories')
        .then((res) => res.json())
        .then((data) => {

           setcategories(data);

        })
    }

    const deleteCategory = (id) => {

        let requestoptions = {
            method : 'DELETE',
            body: JSON.stringify({id:id})
        }

        fetch('https://northwind.vercel.app/api/categories/' + id, requestoptions)
            .then((res) => res.json ())
            .then((data) => {
                 fillData(data);
        })

    }

    const goaddCat = () =>{
        navigation.navigate('addCat')
    }


    return (
        <View>
            <Card>
  <Card.Title>CARD WITH DIVIDER</Card.Title>
  <Card.Divider/>
  {
    users.map((u, i) => {
      return (
        <View key={i} style={styles.user}>
          <Image
            style={styles.image}
            resizeMode="cover"
            source={{ uri: u.avatar }}
          />
          <Text style={styles.name}>{u.name}</Text>
        </View>
      );
    })
  }
</Card>
                {

                    categories.slice(0,7).map((item)  => (
                        <View >
                        <ListItem >
                        <ListItem.Content  >
                         <View>
                          <ListItem.Title>Name: {item.name}</ListItem.Title>
                          <ListItem.Subtitle>Description: {item.description}</ListItem.Subtitle>

                        </View>
                          <View >
                          <Icon name='delete' onPress={() => deleteCategory(item.id)} />
                          </View>
                        </ListItem.Content>
                      </ListItem>
                      </View>
                    ))
                    }


                <Button onPress={() => goaddCat()} title='Go to add Category !' ></Button>

        </View>

    )

}
