//import React from 'react'
//import { View, Text } from 'react-native'
import React, { useState } from 'react';
import { Text, View, StyleSheet, Button, TextInput, ScrollView } from 'react-native';
import Constants from 'expo-constants';

export default function App() {

    const [ name, setName ] = useState("")

    const fncCallSend = () => {
      console.log("FncSend Call " + name);
    }


    return (
        <View style={styles.container}>

    <ScrollView>

      <Text style={styles.paragraph}> Merhaba Ali </Text>

      <TextInput onChangeText={ (txt) => setName(txt) } style={ styles.dataTextStyle } ></TextInput>

      <View style={styles.buttonStyle}>
        <Button onPress={ fncCallSend } title="Send" ></Button>
      </View>

    </ScrollView>
    </View>
    )
};

const styles = StyleSheet.create({
    container: {
      flex: 1,
      justifyContent: 'center',
      paddingTop: Constants.statusBarHeight,
      backgroundColor: '#ecf0f1',
      padding: 8,
    },
    paragraph: {
      margin: 24,
      fontSize: 30,
      fontWeight: 'bold',
      textAlign: 'center',
    },
    buttonStyle: {
      borderWidth:1,
      borderColor:"#859799",
      borderRadius: 5,
      backgroundColor: '#77e1ed',
      marginLeft: 20,
      marginRight: 20,
      padding: 10,
    },
    dataTextStyle: {
      marginLeft: 20,
      marginRight: 20,
      borderWidth:1,
      borderColor:"#859799",
      borderRadius: 5,
      padding: 10,
      marginBottom: 10,
    }
  });
